import { onRenderTriggered } from 'vue';

import { Loading } from 'element-ui';
<template>
  <el-form :model="form" :rules="rules" ref="ruleForm" :inline="true" style="width: 70%" label-width="100px">

    <el-form-item label="操作用户" prop="userName">
      <el-input v-model="form.userName" disabled placeholder="请输入无人机名称"></el-input>
    </el-form-item>

    <el-form-item label="无人机名字" prop="droneName">
      <el-input v-model="form.droneName" disabled placeholder="请输入无人机名字"></el-input>
    </el-form-item>

    <el-form-item label="操作地址" prop="address">
      <el-input style="width:400px" disabled v-model="form.address"  placeholder="请输入描述"></el-input>
    </el-form-item>

    <el-form-item label="预留手机号码" prop="phone">
      <el-input style="width:400px" disabled v-model="form.phone"  placeholder="请输入描述"></el-input>
    </el-form-item>


    <el-form-item label="保养提交时间" prop="createtime">
      <el-date-picker disabled
                      v-model="form.createtime"
                      type="date"
                      value-format="yyyy-MM-dd"
                      placeholder="保养提交时间">
      </el-date-picker>
    </el-form-item>


    <el-form-item label="维修员名字" prop="maintenanceName">
      <el-input v-model="maintenanceName" disabled placeholder="请输入无人机名称"></el-input>
    </el-form-item>



    <el-form-item label="保养完成时间" prop="updatetime">
      <el-date-picker
          v-model="form.updatetime"
          type="date"
          value-format="yyyy-MM-dd"
          placeholder="保养完成时间">
      </el-date-picker>
    </el-form-item>



    <div style="text-align:center; margin-top: 30px;" >
      <el-button type="primary" @click="save">提交</el-button>
      <el-button type="danger">取消</el-button>
    </div>

  </el-form>
</template>

<script>

export default {
  name:'opratedrone',
  data(){
    return{
      user:Cookie.get('user') ? JSON.parse(Cookie.get('user')) :{},
      form:{},
      orders:[],
      rules: {}  ,// 添加 rules 属性
      orederid:null
    }
  },

  created(){
    const id = this.$route.query.id;

    request.get("/maintenance/"+id).then(res =>{
      this.form =res.data
      console.log(res.data)
      const orderId=res.data.orderId

      request.get('/order/getById/'+orderId).then(res =>{
        this.form.score =res.data.score
        console.log(this.form)

      })

    })
    const user = Cookie.get('user') ? JSON.parse(Cookie.get('user')) : {};
    console.log(user)
    this.maintenanceId = user.id;
    this.maintenanceName=user.username;
  },
  Loading(){

  },

  methods:{

    save(){
      this.form.maintenanceId=this.maintenanceId
      this.form.maintenanceName=this.maintenanceName
      request.put('/maintenance/update',this.form).then(res =>{
        console.log(this.form)
        if(res.code ==='200'){
          this.$notify.success('更新成功')
        }else{
          this.$notify.error(res.msg)

        }
      })
      console.log(this.form)

      this.form.status=false
      request.put('/order/statusupdate',{...this.form,id:this.form.orderId}).then(res =>{
        if(res.code ==='200'){
          console.log(this.form.status)
          this.$notify.success('更新成功')

        }else{
          this.$notify.error(res.msg)

        }
      })



    }
  }

}
</script>
